"use strict";

const idlUtils = require("jsdom/lib/jsdom/living/generated/utils");

const NodeImpl = require("jsdom/lib/jsdom/living/nodes/Node-impl").implementation;
const ChildNodeImpl = require("jsdom/lib/jsdom/living/nodes/ChildNode-impl").implementation;

const NODE_TYPE = require("jsdom/lib/jsdom/living/node-type");

class DocumentTypeImpl extends NodeImpl {
  constructor(args, privateData) {
    super(args, privateData);

    this.nodeType = NODE_TYPE.DOCUMENT_TYPE_NODE;

    this.name = privateData.name;
    this.publicId = privateData.publicId;
    this.systemId = privateData.systemId;
  }
}

idlUtils.mixin(DocumentTypeImpl.prototype, ChildNodeImpl.prototype);

module.exports = {
  implementation: DocumentTypeImpl
};
