"use strict";

const HTMLElementImpl = require("jsdom/lib/jsdom/living/nodes/HTMLElement-impl").implementation;

const defineGetter = require("jsdom/lib/jsdom/utils").defineGetter;
const defineSetter = require("jsdom/lib/jsdom/utils").defineSetter;
const proxiedWindowEventHandlers = require("jsdom/lib/jsdom/living/helpers/proxied-window-event-handlers");

class HTMLBodyElementImpl extends HTMLElementImpl {

}

(function () {
  proxiedWindowEventHandlers.forEach(name => {
    defineSetter(HTMLBodyElementImpl.prototype, name, function (handler) {
      const window = this._ownerDocument._defaultView;
      if (window) {
        window[name] = handler;
      }
    });
    defineGetter(HTMLBodyElementImpl.prototype, name, function () {
      const window = this._ownerDocument._defaultView;
      return window ? window[name] : null;
    });
  });
}());

module.exports = {
  implementation: HTMLBodyElementImpl
};
