"use strict";

const HTMLElementImpl = require("jsdom/lib/jsdom/living/nodes/HTMLElement-impl").implementation;
const closest = require("jsdom/lib/jsdom/living/helpers/traversal").closest;

class HTMLLegendElementImpl extends HTMLElementImpl {
  get form() {
    return closest(this, "form");
  }
}

module.exports = {
  implementation: HTMLLegendElementImpl
};
